    private async Task SwitchMode(bool useEditor)
    {
        if (_editorMode && !useEditor && _quillHtml != null)
        {
            // Переключение из визуального в HTML: сохраняем HTML из редактора
            _model.Content = await _quillHtml.GetHTML();
        }
        else if (!_editorMode && useEditor && _quillHtml != null)
        {
            // Переключение из HTML в визуальный: загружаем HTML в редактор
            await _quillHtml.LoadHTMLContent(_model.Content);
        }

    [Parameter] public int? id { get; set; }
    private bool _isNew => !id.HasValue;
    private Post _model = new();
    private bool _loading = true;
    private string? _error;
    private bool _editorMode = true;
            // Если в визуальном режиме, получаем HTML из редактора
            if (_editorMode && _quillHtml != null)
            {
                _model.Content = await _quillHtml.GetHTML();
            }

    private BlazoredTextEditor? _quillHtml;

    protected override async Task OnParametersSetAsync()
    {
        _error = null;
        _loading = true;
        try
        {
            if (_isNew)
            {
                _model = new Post { IsPublished = true };
            }
            else
            {
                var existing = await Db.Posts.FirstOrDefaultAsync(x => x.Id == id!.Value);
                if (existing is null)
                {
                    _error = "Пост не найден.";
                    _model = new Post();
                }
                else
                {
                    _model = existing;
                }
            }
        }
        catch (Exception ex)
        {
            _error = $"Ошибка загрузки поста: {ex.Message}";
        }
        finally
        {
            _loading = false;
        }
    }

    protected override async Task OnAfterRenderAsync(bool firstRender)
    {
        if (firstRender && _quillHtml != null)
        {
            await _quillHtml.LoadHTMLContent(_model.Content);
        }
    }

    private async Task SwitchMode(bool useEditor)
    {
        if (_editorMode && !useEditor && _quillHtml != null)
        {
            // Переключение из визуального в HTML: сохраняем HTML из редактора
            _model.Content = await _quillHtml.GetHTML();
        }
        else if (!_editorMode && useEditor && _quillHtml != null)
        {
            // Переключение из HTML в визуальный: загружаем HTML в редактор
            await _quillHtml.LoadHTMLContent(_model.Content);
        }
        _editorMode = useEditor;
    }

    private async Task SaveAsync()
    {
        try
        {
            // Если в визуальном режиме, получаем HTML из редактора
            if (_editorMode && _quillHtml != null)
            {
                _model.Content = await _quillHtml.GetHTML();
            }

            if (_isNew)
            {
                _model.CreatedAt = DateTime.UtcNow;
                await Db.Posts.AddAsync(_model);
            }
            else
            {
                _model.UpdatedAt = DateTime.UtcNow;
                Db.Posts.Update(_model);
            }

            await Db.SaveChangesAsync();
            Nav.NavigateTo("/admin/posts");
        }
        catch (Exception ex)
        {
            _error = $"Ошибка сохранения: {ex.Message}";
        }
    }
}

